import React, { useState } from "react";

const App = () => {
    const [image, setImage] = useState(null);
    const [outputImages, setOutputImages] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setImage(event.target.files[0]);
    };

    const uploadAndGenerate = async () => {
        if (!image) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", image);

        try {
            const response = await fetch("http://localhost:5001/generate", {
                method: "POST",
                body: formData
            });

            const data = await response.json();
            if (!response.ok) throw new Error(data.error || "Unknown error occurred");

            setOutputImages(data.output_files);
        } catch (error) {
            console.error("Error uploading image:", error);
            alert("Upload failed: " + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", fontFamily: "Arial, sans-serif" }}>
            <h1>Poster & Banner Generator</h1>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <button onClick={uploadAndGenerate} disabled={loading}>
                {loading ? "Generating..." : "Generate Designs"}
            </button>

            <div style={{ marginTop: 20 }}>
                {Array.from({ length: 8 }).map((_, templateIndex) => {
                    const posterIndex = templateIndex * 2;
                    const bannerIndex = posterIndex + 1;

                    return (
                        <div key={templateIndex} style={{ marginBottom: 40 }}>
                            <h2>Template {templateIndex + 1}</h2>
                            <div style={{ display: "flex", flexDirection: "column", alignItems: "center", gap: 20 }}>
                                {outputImages[posterIndex] && (
                                    <div>
                                        <h3>Poster</h3>
                                        <img
                                            src={`http://localhost:5001/static/output/${outputImages[posterIndex]}`}
                                            alt={`Poster ${posterIndex}`}
                                            style={{
                                                width: 300,
                                                height: "auto",
                                                border: "2px solid #ccc",
                                                boxShadow: "2px 2px 10px rgba(0,0,0,0.2)"
                                            }}
                                        />
                                    </div>
                                )}
                                {outputImages[bannerIndex] && (
                                    <div>
                                        <h3>Banner</h3>
                                        <img
                                            src={`http://localhost:5001/static/output/${outputImages[bannerIndex]}`}
                                            alt={`Banner ${bannerIndex}`}
                                            style={{
                                                width: "100%",
                                                height: 600,
                                                objectFit: "cover",
                                                border: "2px solid #ccc",
                                                boxShadow: "2px 2px 10px rgba(0,0,0,0.2)"
                                            }}
                                        />
                                    </div>
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default App;
