import React, { useState } from "react";

const App = () => {
    const [image, setImage] = useState(null);
    const [outputImages, setOutputImages] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setImage(event.target.files[0]);
    };

    const uploadAndGenerate = async () => {
        if (!image) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", image);

        try {
            const response = await fetch("http://localhost:5001/generate", {
                method: "POST",
                body: formData
            });

            const data = await response.json();
            if (!response.ok) throw new Error(data.error || "Unknown error occurred");

            setOutputImages(data.output_files);
        } catch (error) {
            console.error("Error uploading image:", error);
            alert("Upload failed: " + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", fontFamily: "Arial, sans-serif" }}>
            <h1>Poster Generator</h1>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <button onClick={uploadAndGenerate} disabled={loading}>
                {loading ? "Generating..." : "Generate Posters"}
            </button>

            <div style={{ marginTop: 20 }}>
                {Array.from({ length: 16 }).map((_, templateIndex) => {
                    const imgIndex1 = templateIndex * 2;
                    const imgIndex2 = imgIndex1 + 1;

                    return (
                        <div key={templateIndex} style={{ marginBottom: 30 }}>
                            <h2>Template: {templateIndex + 1}</h2>
                            <div style={{ display: "flex", justifyContent: "center", gap: 20 }}>
                                {outputImages[imgIndex1] && (
                                    <img 
                                        src={`http://localhost:5001/static/output/${outputImages[imgIndex1]}`} 
                                        alt={`Output ${imgIndex1}`} 
                                        style={{
                                            width: 200, height: "auto", border: "2px solid #ccc",
                                            position: "relative", top: templateIndex * 5 + "px"
                                        }}
                                    />
                                )}
                                {outputImages[imgIndex2] && (
                                    <img 
                                        src={`http://localhost:5001/static/output/${outputImages[imgIndex2]}`} 
                                        alt={`Output ${imgIndex2}`} 
                                        style={{
                                            width: 200, height: "auto", border: "2px solid #ccc",
                                            position: "relative", top: templateIndex * -5 + "px"
                                        }}
                                    />
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default App;
