import React, { useState } from "react";

const App = () => {
    const [image, setImage] = useState(null);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [genre, setGenre] = useState("");
    const [outputImages, setOutputImages] = useState([]);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setImage(event.target.files[0]);
    };

    const uploadAndGenerate = async () => {
        if (!image) {
            alert("Please select an image first.");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("image", image);
        formData.append("title", title);
        formData.append("description", description);
        formData.append("genre", genre);

        try {
            const response = await fetch("http://localhost:5001/generate", {
                method: "POST",
                body: formData
            });

            const data = await response.json();
            console.log("Backend Response:", data);

            if (!response.ok) throw new Error(data.error || "Unknown error occurred");

            setOutputImages(data.output_files);
        } catch (error) {
            console.error("Error uploading image:", error);
            alert("Upload failed: " + error.message);
        } finally {
            setLoading(false);
        }
    };

    const downloadImage = (imageUrl, filename) => {
        const link = document.createElement("a");
        link.href = imageUrl;
        link.download = filename;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    return (
        <div style={{ textAlign: "center", fontFamily: "Arial, sans-serif" }}>
            <h1>Dynamic Book Poster Generator</h1>

            <input type="file" accept="image/*" onChange={handleFileChange} /><br/>
            <input type="text" placeholder="Book Title" value={title} onChange={(e) => setTitle(e.target.value)} /><br/>
            <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} /><br/>
            <input type="text" placeholder="Genre" value={genre} onChange={(e) => setGenre(e.target.value)} /><br/>

            <button onClick={uploadAndGenerate} disabled={loading}>
                {loading ? "Generating..." : "Generate Posters"}
            </button>

            <div style={{ marginTop: 20 }}>
                {outputImages.map((imageName, index) => (
                    <div key={index} style={{ marginBottom: 20 }}>
                        <h3>{`Design ${index + 1}`}</h3>
                        <img
                            src={`http://localhost:5001/static/output/${imageName}`}
                            alt={`Design ${index + 1}`}
                            style={{
                                maxWidth: "100%",
                                height: "auto",
                                border: "2px solid #ccc",
                                boxShadow: "2px 2px 10px rgba(0,0,0,0.2)",
                                cursor: "pointer"
                            }}
                            onClick={() => downloadImage(`http://localhost:5001/static/output/${imageName}`, imageName)}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default App;
