import React, { useState } from "react";

const App = () => {
    const [image, setImage] = useState(null);
    const [outputImages, setOutputImages] = useState([]);

    const handleFileChange = (event) => {
        setImage(event.target.files[0]);
    };

    const uploadAndGenerate = async () => {
        if (!image) {
            alert("Please select an image first.");
            return;
        }

        const formData = new FormData();
        formData.append("image", image);

        try {
            const response = await fetch("http://localhost:5001/generate", {
                method: "POST",
                body: formData
            });

            const data = await response.json();
            if (!response.ok) throw new Error(data.error || "Unknown error occurred");

            setOutputImages(data.output_files);
        } catch (error) {
            console.error("Error uploading image:", error);
            alert("Upload failed: " + error.message);
        }
    };

    return (
        <div style={{ textAlign: "center", fontFamily: "Arial, sans-serif" }}>
            <h1>Poster Generator</h1>
            <input type="file" accept="image/*" onChange={handleFileChange} />
            <button onClick={uploadAndGenerate}>Generate Posters</button>
            <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: 20 }}>
                {outputImages.map((filename, index) => (
                    <img 
                        key={index} 
                        src={`http://localhost:5001/static/output/${filename}`} 
                        alt={`Output ${index}`} 
                        style={{ margin: 10, width: 200, height: "auto", border: "2px solid #ccc" }}
                    />
                ))}
            </div>
        </div>
    );
};

export default App;
